import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home(){

    const [items,setItems]=useState([]);
    const [inputs,setInputs]=useState('');
    const navigate=useNavigate()
    function handleSubmit(event){
      event.preventDefault();
      event.preventDefault();
      console.log("hi");
      try{
        setInputs(event.target.value)
        axios.post("http://localhost:5003/inventory",{item:inputs})
      .then((response)=>{
        console.log(response.data)
      })
      .catch((error)=>{
        console.log(error)
        if (error.response) {
          // Server responded with a status other than 200 range
          console.error('Error response:', error.response.data);
          console.error('Error status:', error.response.status);
          console.error('Error headers:', error.response.headers);
      } else if (error.request) {
          // Request was made but no response was received
          console.error('Error request:', error.request);
      } else {
          // Something happened in setting up the request
          console.error('Error message:', error.message);
      }
      })
        console.log("done")

      }
      catch(error){
        console.log(error);
      }


    navigate(`/inventory`);
    }// wrap the sql query in a function and try put them in inventory if and only if the phonetic of the item matches with the phonetic of item name or atleast includes it. 
    useEffect(()=>{
      axios.get("http://localhost:5003")
      .then((response)=>{
        setItems(response.data);
      })
      .catch((error)=>{
        console.log(error);
      })
    })
    return (
        <div>
      <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <div class="container-fluid">
    <a class="navbar-brand " href="/">{items.home}</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="/setting">{items.settings}</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">{items.help}</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">{items.order}</a>
        </li>


      </ul>
      <form class="d-flex" onSubmit={handleSubmit}>
        <input id="searchInput" class="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
        <button class="btn btn-outline-success" type="submit">{items.search}</button>
      </form>
    </div>
  </div>
</nav>
    </div>
    )
}
export default Home;
