import axios from "axios";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";

function Setting(){

    const [language,setLanguage]=useState([]);
    const postData = (language) => {
        // Function to perform a POST request
        axios.post("http://localhost:5001/setting", { "key" : language })
          .then((response) => {
            console.log(response.data);
            // Optionally update state or perform other actions after successful POST
          })
          .catch((error) => {
            console.log(error);
          });
      };
    function getLanguage(lang){
        setLanguage(lang);
    }

    return(


        <div>
        <p class="lead">
            Choose your language here
        </p>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={() => getLanguage("kn")} />
            <label class="form-check-label" for="flexRadioDefault1">
                Kannada
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" onChange={() => getLanguage("tel")} />
            <label class="form-check-label" for="flexRadioDefault2">
                Telugu
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault3" onChange={() => postData("hi")} />
            <label class="form-check-label" for="flexRadioDefault3">
                Hindi
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault4" onChange={() => getLanguage("hi")} />
            <label class="form-check-label" for="flexRadioDefault3">
                English
            </label>
        </div>



    </div>
    )
}
export default Setting;
