import axios from "axios";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import Home from "./home";
function Setting(){

    const [language,setLanguage]=useState([]);
    const [items,setItems]=useState([]);

    async function postData(language) {
        // Function to perform a POST request
        axios.post("http://localhost:5003/setting", { "key" : language })
          .then((response) => {
            console.log(response.data);
            // Optionally update state or perform other actions after successful POST
          })
          .catch((error) => {
            console.log(error);
          });
      };
    //   useEffect(()=>{
    //     axios.get("http://localhost:5003")
    //     .then((response)=>{
    //       setItems(response.data);
    //     })
    //     .catch((error)=>{
    //       console.log(error);
    //     })
    //   })
    function getLanguage(lang){
        setLanguage(lang);
    }

    return(


        <div>
        <Home />
        <p class="lead">
            Choose your language here
        </p>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onClick={postData("kn")} />
            <label class="form-check-label" for="flexRadioDefault1">
                Kannada
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" onClick={postData("te")} />
            <label class="form-check-label" for="flexRadioDefault2">
                Telugu
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault3" onClick={postData("hi")} />
            <label class="form-check-label" for="flexRadioDefault3">
                Hindi
            </label>
        </div>
        <div class="form-check">
            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault4" onClick={postData("en")} />
            <label class="form-check-label" for="flexRadioDefault3">
                English
            </label>
        </div>



    </div>
    )
}
export default Setting;
