import react, { useState } from "react";

import { useEffect } from "react";
import axios from "axios";
import './cart.css'

import Cart from "./cart";
function Carts(){

	const [result,setResult]=useState([]);
	useEffect(()=>{

		axios.get("http://localhost:5003/cart")
		.then((response)=>{
			setResult(response.data);

		})
		.catch((error)=>{
			console.log(error);
		})
	},[])
    useEffect(() => {
        console.log('Updated Result:', result); // Option 2
    }, [result]);


    return(
		<div class="shopping-cart section">
		<div class="container">
			<div class="row">
        <div className="col-12 shopping-cart" >

		<table className="shopping-summery thead" >
			<thead>
				<tr className="main-hading">
					<th>Product</th>
					<th>Item</th>
					<th className="text-center">Unit Price</th>
					<th className="text-center"></th>

					<th className="text-center"><i className="ti-trash remove-icon"></i></th>
				</tr>
			</thead>

                {result.map((row)=>(
                    <Cart 
                        id={row.id}
                        item={row.item}
                        price={row.price}
                        quantity={row.quantity}
                    />
                ))}


        </table>
    </div>
	</div>
	</div>
	</div>

    )
}
export default Carts;
