import React from "react";
import { useState } from "react";
function Setting(){
    const [language,setLanguage]=useState([]);
    function getLanguage(lang){
        setLanguage(lang);
    }
    return(


    <div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={()=>getLanguage("kn")}/>

        <label class="form-check-label" for="flexRadioDefault1">
            Kannada
        </label>
        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={()=>getLanguage("tel")}/>

        <label class="form-check-label" for="flexRadioDefault1">
            Telugu
        </label>
        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={()=>getLanguage("hi")}/>

        <label class="form-check-label" for="flexRadioDefault1">
            Hindi
        </label>
        </div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked="" onChange={()=>getLanguage("en")}/>
        <label class="form-check-label" for="flexRadioDefault2">
            English
        </label>
        </div>
    </div>
    )
}
export default Setting;
