import React from 'react';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
function Cart(props){
	const [quantity,setQuantity]=useState(1);

	function handleClickPlus(){
		if(!quantity){
			setQuantity(1);
		}
		if(quantity===10){
			//do nothing
		}
		else{
			setQuantity(quantity+1);

		}
	}
	function handleClickMinus(){
		if(quantity===0){
			//do nothing
		}
		else{
			setQuantity(quantity-1);
		}
	}
    return(

		<div>
			<tbody>
				<tr id={`item-${props.id}`}>
					<td class="image" data-title="No"><img src="/laptop.jpeg" alt="#"/></td>
					<td class="product-des" data-title="Description">
						<p class="product-name">{props.item}</p>

					</td>
					<td class="price" data-title="Price"><span>{props.price} </span></td>
					<td class="qty" data-title="Qty">
						<div class="input-group">
							<div class="button minus">
								<button type="button" class="btn btn-primary btn-number" data-type="minus" data-field="quant[1]" onClick={handleClickMinus}>
									&#8722;
								</button>
							</div>
							<input type="text" name="quant[1]" class="input-number"  data-min="1" data-max="100" value={quantity} readOnly/>
							<div class="button plus">
								<button type="button" class="btn btn-primary btn-number" data-type="plus" data-field="quant[1]" onClick={handleClickPlus}>
									&#43;
								</button>
							</div>
						</div>

					</td>

					<td class="action" data-title="Remove"><a href="#"><i class="ti-trash remove-icon"></i></a></td>
				</tr>
			</tbody>
		</div>

    )
}
export default Cart;
